import React, { useState, useEffect } from "react";
import "./App.css"; // You will write the custom styles here

const App = () => {
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const timerId = setInterval(() => {
      setTime(new Date());
    }, 1000);

    return () => clearInterval(timerId); // Cleanup the interval on component unmount
  }, []);

  const formatTime = (date) => {
    return new Intl.DateTimeFormat("en-IN", {
      hour: "2-digit",
      minute: "2-digit",
      second: "2-digit",
      timeZone: "Asia/Kolkata", // Set to IST
      hour12: true,
    }).format(date);
  };

  const formatDate = (date) => {
    return new Intl.DateTimeFormat("en-IN", {
      weekday: "long",
      year: "numeric",
      month: "long",
      day: "numeric",
      timeZone: "Asia/Kolkata",
    }).format(date);
  };

  const hourDeg = (time.getHours() % 12) * 30 + (time.getMinutes() / 2);
  const minuteDeg = time.getMinutes() * 6;
  const secondDeg = time.getSeconds() * 6;

  return (
    <div className="h-screen bg-gradient-to-br from-blue-500 to-purple-700 flex items-center justify-center">
      <div className="text-center text-white">
        <div className="clock-container mb-8">
          <div className="clock">
            <div className="hour-hand" style={{ transform: `rotate(${hourDeg}deg)` }}></div>
            <div className="minute-hand" style={{ transform: `rotate(${minuteDeg}deg)` }}></div>
            <div className="second-hand" style={{ transform: `rotate(${secondDeg}deg)` }}></div>
            <div className="center-circle"></div>
          </div>
        </div>
        <h1 className="text-4xl md:text-6xl font-bold mb-4">{formatTime(time)}</h1>
        <p className="text-lg md:text-2xl opacity-80">{formatDate(time)}</p>
      </div>
    </div>
  );
};

export default App;
