import React, { useState, useEffect } from "react";

const App = () => {
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const timerId = setInterval(() => {
      setTime(new Date());
    }, 1000);

    return () => clearInterval(timerId); // Cleanup the interval on component unmount
  }, []);

  const formatTime = (date) => {
    return new Intl.DateTimeFormat("en-IN", {
      hour: "2-digit",
      minute: "2-digit",
      second: "2-digit",
      timeZone: "Asia/Kolkata", // Set to IST
      hour12: true, // For 12-hour format, set to `false` for 24-hour format
    }).format(date);
  };

  const formatDate = (date) => {
    return new Intl.DateTimeFormat("en-IN", {
      weekday: "long",
      year: "numeric",
      month: "long",
      day: "numeric",
      timeZone: "Asia/Kolkata", // Set to IST
    }).format(date);
  };

  return (
    <div className="h-screen bg-gradient-to-br from-blue-500 to-purple-700 flex items-center justify-center">
      <div className="text-center">
        <h1 className="text-4xl md:text-6xl font-bold text-white mb-4">
          {formatTime(time)}
        </h1>
        <p className="text-lg md:text-2xl text-white opacity-80">
          {formatDate(time)}
        </p>
      </div>
    </div>
  );
};

export default App;
